import React, { useState } from 'react';
import Web3 from 'web3';

const App = () => {
  const [address, setAddress] = useState('');
  const [events, setEvents] = useState([]);
  const [error, setError] = useState('');
  const [logs, setLogs] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [filters, setFilters] = useState({
    sender: '',
    receiver: '',
    minValue: '',
    startDate: '',
    endDate: '',
  });
  const pageSize = 10;

  const handleFilterChange = (field, value) => {
    setFilters((prevFilters) => ({
      ...prevFilters,
      [field]: value,
    }));
    setCurrentPage(1); // Reset to the first page when filters change
  };

  const fetchTransferEvents = async () => {
    setError('');
    setLogs([]);
    setEvents([]);

    if (!address) {
      setError('Please enter a valid contract address.');
      return;
    }

    try {
      setLogs((prev) => [...prev, 'Initializing Web3...']);
      const web3 = new Web3('http://37.27.107.225:7777'); // Your node address

      setLogs((prev) => [...prev, `Querying Transfer events for address: ${address}`]);
      const contract = new web3.eth.Contract(
        [
          {
            anonymous: false,
            inputs: [
              { indexed: true, name: 'from', type: 'address' },
              { indexed: true, name: 'to', type: 'address' },
              { indexed: false, name: 'value', type: 'uint256' },
            ],
            name: 'Transfer',
            type: 'event',
          },
        ],
        address
      );

      const pastEvents = await contract.getPastEvents('Transfer', {
        fromBlock: 'latest',
        toBlock: 'latest',
      });

      const formattedEvents = pastEvents.map((event) => ({
        from: event.returnValues.from,
        to: event.returnValues.to,
        value: event.returnValues.value.toString(), // Convert BigInt to string
        transactionHash: event.transactionHash,
        blockNumber: event.blockNumber,
        timestamp: new Date().toISOString(), // Dummy timestamp
      }));

      setLogs((prev) => [...prev, `Found ${formattedEvents.length} Transfer events.`]);
      setEvents(formattedEvents);
    } catch (err) {
      setError(`Error occurred: ${err.message}`);
      setLogs((prev) => [...prev, `Error occurred: ${err.message}`]);
    }
  };

  const applyFilters = () => {
    return events.filter((event) => {
      const { sender, receiver, minValue, startDate, endDate } = filters;
      const eventValue = parseFloat(event.value);

      return (
        (!sender || event.from.toLowerCase().includes(sender.toLowerCase())) &&
        (!receiver || event.to.toLowerCase().includes(receiver.toLowerCase())) &&
        (!minValue || eventValue >= parseFloat(minValue)) &&
        (!startDate || new Date(event.timestamp) >= new Date(startDate)) &&
        (!endDate || new Date(event.timestamp) <= new Date(endDate))
      );
    });
  };

  const paginatedEvents = applyFilters().slice(
    (currentPage - 1) * pageSize,
    currentPage * pageSize
  );

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Taraxa Live Chart Debugger</h1>

      <div>
        <input
          type="text"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
          placeholder="Enter Contract Address"
          style={{ width: '300px', marginRight: '10px', padding: '5px' }}
        />
        <button onClick={fetchTransferEvents} style={{ padding: '5px 10px' }}>
          Fetch Data
        </button>
      </div>

      <div>
        <h3>Filters:</h3>
        <input
          type="text"
          placeholder="Sender"
          onChange={(e) => handleFilterChange('sender', e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <input
          type="text"
          placeholder="Receiver"
          onChange={(e) => handleFilterChange('receiver', e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <input
          type="number"
          placeholder="Min Value"
          onChange={(e) => handleFilterChange('minValue', e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <input
          type="date"
          placeholder="Start Date"
          onChange={(e) => handleFilterChange('startDate', e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <input
          type="date"
          placeholder="End Date"
          onChange={(e) => handleFilterChange('endDate', e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
        />
      </div>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      <div>
        <h3>Logs:</h3>
        <ul>
          {logs.map((log, index) => (
            <li key={index}>{log}</li>
          ))}
        </ul>
      </div>

      <div>
        <h3>Transfer Events:</h3>
        <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th>From</th>
              <th>To</th>
              <th>Value</th>
              <th>Timestamp</th>
              <th>Transaction Hash</th>
            </tr>
          </thead>
          <tbody>
            {paginatedEvents.map((event, index) => (
              <tr key={index}>
                <td>{event.from}</td>
                <td>{event.to}</td>
                <td>{event.value}</td>
                <td>{event.timestamp}</td>
                <td>{event.transactionHash}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <div>
          <button
            onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
            disabled={currentPage === 1}
          >
            Previous
          </button>
          <span style={{ margin: '0 10px' }}>Page {currentPage}</span>
          <button
            onClick={() =>
              setCurrentPage((prev) =>
                prev * pageSize < applyFilters().length ? prev + 1 : prev
              )
            }
            disabled={paginatedEvents.length < pageSize}
          >
            Next
          </button>
        </div>
      </div>
    </div>
  );
};

export default App;
