import React, { useState, useEffect } from 'react';
import Web3 from 'web3';

const App = () => {
  const [address, setAddress] = useState('');
  const [events, setEvents] = useState([]);
  const [error, setError] = useState('');
  const [logs, setLogs] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize] = useState(10); // Number of items per page
  const [filters, setFilters] = useState({
    sender: '',
    receiver: '',
    minValue: '',
    startDate: '',
    endDate: '',
  });

  const fetchTransferEvents = async () => {
    if (!address) {
      setError('Please enter a valid contract address.');
      return;
    }

    try {
      setLogs((prevLogs) => [...prevLogs, 'Initializing Web3...']);
      const web3 = new Web3('http://37.27.107.225:7777');

      setLogs((prevLogs) => [...prevLogs, `Querying Transfer events for address: ${address}`]);

      const transferEvents = await web3.eth.getPastLogs({
        address,
        topics: [
          web3.utils.keccak256('Transfer(address,address,uint256)'),
        ],
      });

      const formattedEvents = await Promise.all(
        transferEvents.map(async (event) => {
          const block = await web3.eth.getBlock(event.blockNumber);
          return {
            from: `0x${event.topics[1].slice(26)}`,
            to: `0x${event.topics[2].slice(26)}`,
            value: web3.utils.hexToNumberString(event.data),
            transactionHash: event.transactionHash,
            timestamp: new Date(block.timestamp * 1000).toLocaleString(),
          };
        })
      );

      setEvents(formattedEvents);
      setLogs((prevLogs) => [...prevLogs, `Found ${formattedEvents.length} Transfer events.`]);
    } catch (err) {
      setError(`Error occurred: ${err.message}`);
      setLogs((prevLogs) => [...prevLogs, `Error occurred: ${err.message}`]);
    }
  };

  const applyFilters = () => {
    const { sender, receiver, minValue, startDate, endDate } = filters;
    return events
      .filter(
        (event) =>
          (!sender || event.from.toLowerCase() === sender.toLowerCase()) &&
          (!receiver || event.to.toLowerCase() === receiver.toLowerCase()) &&
          (!minValue || Number(event.value) >= Number(minValue)) &&
          (!startDate || new Date(event.timestamp) >= new Date(startDate)) &&
          (!endDate || new Date(event.timestamp) <= new Date(endDate))
      )
      .slice((currentPage - 1) * pageSize, currentPage * pageSize);
  };

  const handleLiveUpdates = () => {
    const intervalId = setInterval(fetchTransferEvents, 5000); // Fetch data every 5 seconds
    return () => clearInterval(intervalId); // Cleanup on component unmount
  };

  useEffect(handleLiveUpdates, [address]);

  const handleFilterChange = (key, value) => {
    setFilters((prev) => ({ ...prev, [key]: value }));
  };

  const filteredEvents = applyFilters();

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Taraxa Live Chart Debugger</h1>

      <div>
        <input
          type="text"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
          placeholder="Enter Contract Address"
          style={{ width: '300px', marginRight: '10px', padding: '5px' }}
        />
        <button onClick={fetchTransferEvents} style={{ padding: '5px 10px' }}>
          Fetch Data
        </button>
      </div>

      <div>
        <h3>Filters:</h3>
        <input
          type="text"
          placeholder="Sender"
          onChange={(e) => handleFilterChange('sender', e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <input
          type="text"
          placeholder="Receiver"
          onChange={(e) => handleFilterChange('receiver', e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <input
          type="number"
          placeholder="Min Value"
          onChange={(e) => handleFilterChange('minValue', e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <input
          type="date"
          placeholder="Start Date"
          onChange={(e) => handleFilterChange('startDate', e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <input
          type="date"
          placeholder="End Date"
          onChange={(e) => handleFilterChange('endDate', e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
        />
      </div>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      <div>
        <h3>Logs:</h3>
        <ul>
          {logs.map((log, index) => (
            <li key={index}>{log}</li>
          ))}
        </ul>
      </div>

      <div>
        <h3>Transfer Events:</h3>
        <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th>From</th>
              <th>To</th>
              <th>Value</th>
              <th>Timestamp</th>
              <th>Transaction Hash</th>
            </tr>
          </thead>
          <tbody>
            {filteredEvents.map((event, index) => (
              <tr key={index}>
                <td>{event.from}</td>
                <td>{event.to}</td>
                <td>{event.value}</td>
                <td>{event.timestamp}</td>
                <td>{event.transactionHash}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <div>
          <button
            onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
            disabled={currentPage === 1}
          >
            Previous
          </button>
          <span style={{ margin: '0 10px' }}>Page {currentPage}</span>
          <button
            onClick={() =>
              setCurrentPage((prev) =>
                prev * pageSize < applyFilters().length ? prev + 1 : prev
              )
            }
            disabled={filteredEvents.length < pageSize}
          >
            Next
          </button>
        </div>
      </div>
    </div>
  );
};

export default App;
