import React, { useState, useEffect } from 'react';
import Web3 from 'web3';

const App = () => {
  const [address, setAddress] = useState('');
  const [events, setEvents] = useState([]);
  const [filteredEvents, setFilteredEvents] = useState([]);
  const [error, setError] = useState('');
  const [logs, setLogs] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const pageSize = 10;
  const [filters, setFilters] = useState({
    sender: '',
    receiver: '',
    minValue: '',
    startDate: '',
    endDate: '',
  });

  useEffect(() => {
    const interval = setInterval(fetchTransferEvents, 5000);
    return () => clearInterval(interval); // Cleanup on component unmount
  }, [address]);

  const fetchTransferEvents = async () => {
    if (!address) {
      setError('Please enter a valid contract address.');
      return;
    }

    try {
      setError('');
      setLogs((prevLogs) => [...prevLogs, 'Initializing Web3...']);
      const web3 = new Web3('http://37.27.107.225:7777');

      setLogs((prevLogs) => [...prevLogs, `Querying Transfer events for address: ${address}`]);

      const latestBlock = await web3.eth.getBlockNumber();
      const fromBlock = Math.max(0, latestBlock - 1000);
      const toBlock = latestBlock;

      const transferEvents = await web3.eth.getPastLogs({
        address,
        topics: [web3.utils.keccak256('Transfer(address,address,uint256)')],
        fromBlock: web3.utils.toHex(fromBlock),
        toBlock: web3.utils.toHex(toBlock),
      });

      const formattedEvents = await Promise.all(
        transferEvents.map(async (event) => {
          const block = await web3.eth.getBlock(event.blockNumber);
          return {
            from: `0x${event.topics[1].slice(26)}`,
            to: `0x${event.topics[2].slice(26)}`,
            value: web3.utils.hexToNumberString(event.data),
            transactionHash: event.transactionHash,
            timestamp: new Date(block.timestamp * 1000).toLocaleString(),
          };
        })
      );

      setEvents(formattedEvents);
      setFilteredEvents(applyFilters(formattedEvents));
      setLogs((prevLogs) => [
        ...prevLogs,
        `Found ${formattedEvents.length} Transfer events.`,
      ]);
    } catch (err) {
      setError(`Error occurred: ${err.message}`);
      setLogs((prevLogs) => [...prevLogs, `Error occurred: ${err.message}`]);
    }
  };

  const applyFilters = (data = events) => {
    return data.filter((event) => {
      const { sender, receiver, minValue, startDate, endDate } = filters;
      const matchesSender = sender
        ? event.from.toLowerCase().includes(sender.toLowerCase())
        : true;
      const matchesReceiver = receiver
        ? event.to.toLowerCase().includes(receiver.toLowerCase())
        : true;
      const matchesMinValue = minValue ? parseFloat(event.value) >= minValue : true;
      const matchesStartDate = startDate
        ? new Date(event.timestamp) >= new Date(startDate)
        : true;
      const matchesEndDate = endDate
        ? new Date(event.timestamp) <= new Date(endDate)
        : true;

      return matchesSender && matchesReceiver && matchesMinValue && matchesStartDate && matchesEndDate;
    });
  };

  const handleFilterChange = (key, value) => {
    setFilters((prevFilters) => ({ ...prevFilters, [key]: value }));
    setFilteredEvents(applyFilters());
  };

  const paginatedEvents = filteredEvents.slice(
    (currentPage - 1) * pageSize,
    currentPage * pageSize
  );

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Taraxa Live Chart Debugger</h1>

      <div>
        <input
          type="text"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
          placeholder="Enter Contract Address"
          style={{ width: '300px', marginRight: '10px', padding: '5px' }}
        />
        <button onClick={fetchTransferEvents} style={{ padding: '5px 10px' }}>
          Fetch Data
        </button>
      </div>

      <div>
        <h3>Filters:</h3>
        <input
          type="text"
          placeholder="Sender"
          onChange={(e) => handleFilterChange('sender', e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <input
          type="text"
          placeholder="Receiver"
          onChange={(e) => handleFilterChange('receiver', e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <input
          type="number"
          placeholder="Min Value"
          onChange={(e) => handleFilterChange('minValue', e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <input
          type="date"
          placeholder="Start Date"
          onChange={(e) => handleFilterChange('startDate', e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <input
          type="date"
          placeholder="End Date"
          onChange={(e) => handleFilterChange('endDate', e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
        />
      </div>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      <div>
        <h3>Logs:</h3>
        <ul>
          {logs.map((log, index) => (
            <li key={index}>{log}</li>
          ))}
        </ul>
      </div>

      <div>
        <h3>Transfer Events:</h3>
        <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th>From</th>
              <th>To</th>
              <th>Value</th>
              <th>Timestamp</th>
              <th>Transaction Hash</th>
            </tr>
          </thead>
          <tbody>
            {paginatedEvents.map((event, index) => (
              <tr key={index}>
                <td>{event.from}</td>
                <td>{event.to}</td>
                <td>{event.value}</td>
                <td>{event.timestamp}</td>
                <td>{event.transactionHash}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <div>
          <button
            onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
            disabled={currentPage === 1}
          >
            Previous
          </button>
          <span style={{ margin: '0 10px' }}>Page {currentPage}</span>
          <button
            onClick={() =>
              setCurrentPage((prev) =>
                prev * pageSize < filteredEvents.length ? prev + 1 : prev
              )
            }
            disabled={currentPage * pageSize >= filteredEvents.length}
          >
            Next
          </button>
        </div>
      </div>
    </div>
  );
};

export default App;
