import React, { useState, useEffect } from 'react';
import Web3 from 'web3';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  LineElement,
  CategoryScale,
  LinearScale,
  PointElement,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement, Tooltip, Legend);

const App = () => {
  const [address, setAddress] = useState('');
  const [events, setEvents] = useState([]);
  const [error, setError] = useState('');
  const [logs, setLogs] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [chartData, setChartData] = useState(null);
  const [tokenPrice, setTokenPrice] = useState(null);
  const pageSize = 10;

  const knownPoolAddress = "0xE9ba4dB22e58D5887fc67dbf8dB2b9516352315e"; // Replace with the actual pool address

  useEffect(() => {
    const interval = setInterval(() => {
      if (address) {
        fetchTransferEvents();
      }
    }, 5000); // Poll every 5 seconds

    return () => clearInterval(interval);
  }, [address]);

  const fetchLiquidityPoolData = async (web3, poolAddress) => {
    const poolContract = new web3.eth.Contract(
      [
        {
          constant: true,
          inputs: [],
          name: 'getReserves',
          outputs: [
            { name: '_reserve0', type: 'uint112' },
            { name: '_reserve1', type: 'uint112' },
            { name: '_blockTimestampLast', type: 'uint32' },
          ],
          type: 'function',
        },
        {
          constant: true,
          inputs: [],
          name: 'token0',
          outputs: [{ name: '', type: 'address' }],
          type: 'function',
        },
        {
          constant: true,
          inputs: [],
          name: 'token1',
          outputs: [{ name: '', type: 'address' }],
          type: 'function',
        },
      ],
      poolAddress
    );

    const [reserve0, reserve1] = await poolContract.methods.getReserves().call();
    const token0 = await poolContract.methods.token0().call();
    const token1 = await poolContract.methods.token1().call();

    return { reserve0, reserve1, token0, token1 };
  };

  const calculatePrice = (reserve0, reserve1, targetToken, token0, token1) => {
    if (targetToken.toLowerCase() === token0.toLowerCase()) {
      return parseFloat(reserve1) / parseFloat(reserve0);
    } else if (targetToken.toLowerCase() === token1.toLowerCase()) {
      return parseFloat(reserve0) / parseFloat(reserve1);
    }
    return null;
  };

  const fetchTransferEvents = async () => {
    setError('');
    setLogs([]);

    if (!address) {
      setError('Please enter a valid contract address.');
      return;
    }

    try {
      setLogs((prev) => [...prev, 'Initializing Web3...']);
      const web3 = new Web3('http://37.27.107.225:7777'); // Your node address

      // Fetch liquidity pool data and calculate price
      const poolData = await fetchLiquidityPoolData(web3, knownPoolAddress);
      const price = calculatePrice(poolData.reserve0, poolData.reserve1, address, poolData.token0, poolData.token1);
      setTokenPrice(price);

      setLogs((prev) => [...prev, `Current Token Price: ${price.toFixed(6)}`]);

      setLogs((prev) => [...prev, `Querying Transfer events for address: ${address}`]);
      const contract = new web3.eth.Contract(
        [
          {
            anonymous: false,
            inputs: [
              { indexed: true, name: 'from', type: 'address' },
              { indexed: true, name: 'to', type: 'address' },
              { indexed: false, name: 'value', type: 'uint256' },
            ],
            name: 'Transfer',
            type: 'event',
          },
        ],
        address
      );

      const latestBlock = await web3.eth.getBlockNumber();
      const fromBlock = Math.max(Number(latestBlock) - 500000, 0);

      setLogs((prev) => [...prev, `Querying from block ${fromBlock} to ${latestBlock}`]);

      const pastEvents = await contract.getPastEvents('Transfer', {
        fromBlock: fromBlock.toString(),
        toBlock: 'latest',
      });

      setLogs((prev) => [...prev, `Fetched ${pastEvents.length} Transfer events.`]);

      const buyAndSellEvents = pastEvents.filter((event) => {
        const { from, to } = event.returnValues;
        return (
          from.toLowerCase() === knownPoolAddress.toLowerCase() ||
          to.toLowerCase() === knownPoolAddress.toLowerCase()
        );
      });

      setLogs((prev) => [...prev, `Filtered ${buyAndSellEvents.length} Buy/Sell events.`]);

      const formattedEvents = buyAndSellEvents.map((event) => ({
        from: event.returnValues.from,
        to: event.returnValues.to,
        value: BigInt(event.returnValues.value).toString(),
        transactionHash: event.transactionHash,
        blockNumber: event.blockNumber,
        timestamp: new Date().toISOString(),
        type: event.returnValues.to.toLowerCase() === knownPoolAddress.toLowerCase() ? 'Buy' : 'Sell',
      }));

      setEvents((prev) => {
        const allEvents = [...formattedEvents, ...prev];
        const uniqueEvents = Array.from(new Set(allEvents.map((e) => e.transactionHash)))
          .map((hash) => allEvents.find((e) => e.transactionHash === hash));
        return uniqueEvents.slice(0, 1000); // Keep latest 1000 transactions
      });

      const chartLabels = formattedEvents.map((e) => e.timestamp);
      const chartValues = formattedEvents.map((e) => parseFloat(e.value));

      setChartData({
        labels: chartLabels,
        datasets: [
          {
            label: 'Transaction Values',
            data: chartValues,
            borderColor: 'rgba(75, 192, 192, 1)',
            borderWidth: 2,
            fill: false,
          },
        ],
      });
    } catch (err) {
      setError(`Error occurred: ${err.message}`);
      setLogs((prev) => [...prev, `Error occurred: ${err.message}`]);
    }
  };

  const paginatedEvents = events.slice((currentPage - 1) * pageSize, currentPage * pageSize);

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Taraxa Live Chart Debugger</h1>

      <div>
        <input
          type="text"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
          placeholder="Enter Contract Address"
          style={{ width: '300px', marginRight: '10px', padding: '5px' }}
        />
        <button onClick={fetchTransferEvents} style={{ padding: '5px 10px' }}>
          Fetch Data
        </button>
      </div>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      <div>
        <h3>Logs:</h3>
        <ul>
          {logs.map((log, index) => (
            <li key={index}>{log}</li>
          ))}
        </ul>
      </div>

      <div>
        <h3>Transfer Events:</h3>
        <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th>From</th>
              <th>To</th>
              <th>Value</th>
              <th>Price/Token</th>
              <th>Timestamp</th>
              <th>Transaction Hash</th>
              <th>Type</th>
            </tr>
          </thead>
          <tbody>
            {paginatedEvents.map((event, index) => (
              <tr
                key={index}
                style={{
                  backgroundColor: event.type === 'Sell' ? 'red' : event.type === 'Buy' ? 'green' : 'white',
                  color: 'white',
                }}
              >
                <td>{event.from}</td>
                <td>{event.to}</td>
                <td>{event.value}</td>
                <td>${tokenPrice ? tokenPrice.toFixed(6) : 'N/A'}</td>
                <td>{event.timestamp}</td>
                <td>{event.transactionHash}</td>
                <td>{event.type}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <div>
          <button
            onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
            disabled={currentPage === 1}
          >
            Previous
          </button>
          <span style={{ margin: '0 10px' }}>Page {currentPage}</span>
          <button
            onClick={() =>
              setCurrentPage((prev) =>
                prev * pageSize < events.length ? prev + 1 : prev
              )
            }
            disabled={currentPage * pageSize >= events.length}
          >
            Next
          </button>
        </div>
      </div>

      <div>
        <h3>Transaction Chart:</h3>
        {chartData ? (
          <Line data={chartData} />
        ) : (
          <p>No chart data available.</p>
        )}
      </div>
    </div>
  );
};

export default App;
